mapper.select:
SELECT  * from `order` WHERE  status != -1

      AND id = #{id} 


     AND date >= #{startTime}   


     AND  date <= #{endTime}   


     AND fleet_license like CONCAT('%','${fleetLicense}','%' )   


     AND supplier_name like CONCAT('%','${supplierName}','%' )   


     AND supplier_certify like CONCAT('%','${supplierCertify}','%' )   


     AND receiver_name like CONCAT('%','${receiverName}','%' )   


     AND receiver_certify like CONCAT('%','${receiverCertify}','%' )
